let data;function resultLength(){data.results.length;data.results.sort(function(a,b){return a.name.first<b.name.first?-1:a.name.first>b.name.first?1:a.name.first==b.name.first?0:void 0});for(const item of data.results){const mainBody=document.querySelector(".main-body"),image=document.createElement("img"),contactBox=document.createElement("div"),name=document.createElement("p"),phoneNumber=document.createElement("p");name.classList.add("name"),phoneNumber.classList.add("number"),image.classList.add("image"),contactBox.classList.add("contact-box"),name.innerText=`${item.name.first} ${item.name.last}`,phoneNumber.innerText=`${item.phone}`,image.src=item.picture.thumbnail,mainBody.appendChild(image),mainBody.appendChild(contactBox),contactBox.appendChild(name),contactBox.appendChild(phoneNumber)}}fetch("https://randomuser.me/api/?results=10").then(function(response){return response.json()}).then(function(json){data=json,resultLength(),console.log(json)});